serverCode:
// Put your code here.
// Expecting $keys from ClientBefore (array of selected rows)
// Adjust field names here to match your schema.
$ok = true;
$message = "";
$today = date('Y-m-d');

// If your PK isn't "id", set it here:
$pkName = "id";

// If your fields have different names, change here:

$statusField = "status";
$finishField = "actual_finish";

// Optional: permissions check (example)
// if( !Security::isAdmin() ) { echo my_json_encode(["ok"=>false, "message"=>"Not authorized"]); return; }

foreach ($keys as $k) {
    // $k is an associative array of PK values for one row (usually 1 key)
    $rowId = (int)$k[$pkName];

    // Update the row
    $sql = "UPDATE design_subphase
            SET ".$statusField."='Done',
                ".$finishField."=".$_connection->prepareString($today)."
            WHERE ".$pkName."=".$rowId;

    if( !DB::Exec($sql) ) {
        $ok = false;
        $message = "DB update failed on ID ".$rowId;
        break;
    }
}

// Return JSON to the ClientBefore callback
echo my_json_encode([ "ok" => $ok, "message" => $message ]);

beforeJsCode:
// Put your code here.

// For a row button, get that row's key
var id = runner.getSelectedKeys()[0]; // PHPR sets the row as selected when you click the row button
if (!id) { return false; }

ajaxRequest({
  action: 'markDone',
  table: 'design_subphase',
  keys: [ id ] // single row
}, function(resp){
  if (resp && resp.ok) {
    location.reload();
  } else {
    swal(resp && resp.message ? resp.message : 'Update failed');
  }
});
return false;

