(function(){
  // Grab current phase code from a read-only field on the form
  var codeEl = document.querySelector('[data-field="phase_code"] input, [data-field="phase_code"] span');
  var code = codeEl ? (codeEl.value || codeEl.textContent || "").trim() : "";

  function show(fieldName, show){
    var holder = document.querySelector('[data-field="'+fieldName+'"]');
    if(holder){ holder.style.display = show ? "" : "none"; }
  }

  // Approval phases: TF, SD, QA_QC1, QA_QC2
  var isApproval = ["TF","SD","QA_QC1","QA_QC2"].indexOf(code) >= 0;
  // Submission phases: DD, PP
  var isSubmission = ["DD","PP"].indexOf(code) >= 0;

  // Due date is calculated; keep visible (read-only) if you prefer
  // Toggle approval/submission fields
  show("approval_date", isApproval);
  show("submission_date", isSubmission);

  // Optional helper text
  var ap = document.querySelector('[data-field="approval_date"] .rnr-label');
  if(ap && isApproval){ ap.title = "Enter the approval/completion date for this phase"; }
  var sb = document.querySelector('[data-field="submission_date"] .rnr-label');
  if(sb && isSubmission){ sb.title = "Enter the date this phase was submitted"; }
})();

/** PHASES — After record updated (debug + force recalc once)
 *  - Logs what happened to PHP error_log
 *  - Resolves project_id robustly
 *  - Tries to call your recalc function (forced once for test)
 *  - Optional: auto-completes open subphases when a phase completes
 */

// ====== CONFIG: adjust if your field names differ ======
$phasePkField        = "phase_id";
$projectFkField      = "project_id";
$completedDateField  = "completed_date";
$dueDateField        = "due_date"; // kept as a possible trigger, not required
$recalcTriggerFields = [ "completed_date", "due_date" ];
$autoCompleteSubs    = true;
$DEBUG               = true;   // <-- turn ON for now; we’ll turn it off after it works
// =======================================================

// --- logger
$log = function($msg) use ($DEBUG) {
    if ($DEBUG) error_log("[PHASES AfterUpdate] ".$msg);
};

// --- include user functions where recalc_* should live
$uc = getabspath("include/usercode.php");
if (file_exists($uc)) { require_once($uc); $log("usercode.php included"); }
else { $log("usercode.php NOT found at ".$uc); }

// --- connection (best-effort)
$conn = null;
if (isset($pageObject) && method_exists($pageObject, 'getConnection')) {
    $conn = $pageObject->getConnection();
}
if (!$conn && class_exists("DB")) {
    // Fallback to table-bound connection if available
    if (method_exists("DB","ConnectionByTable")) {
        $conn = DB::ConnectionByTable("phases");
        $log("ConnectionByTable used");
    } else {
        $log("No connection helper; SQL will use DB::* static");
    }
}
$log("conn=".($conn ? "OK" : "NULL"));

// --- helper: did a field change in this update?
$fieldChanged = function($field) use ($values, $oldvalues) {
    if (!$field) return false;
    if (!array_key_exists($field, $values)) return false;
    $old = $oldvalues[$field] ?? null;
    $new = $values[$field];
    return $new !== $old;
};

// 1) trigger detection (for info)
$shouldRecalc = false;
foreach ($recalcTriggerFields as $f) {
    if ($fieldChanged($f)) { $shouldRecalc = true; $log("trigger changed: ".$f); break; }
}

// 2) resolve ids
$phaseId = (int)(
    $keys[$phasePkField]      ?? 
    $values[$phasePkField]    ?? 
    $oldvalues[$phasePkField] ?? 0
);
$log("phaseId=".$phaseId);

$projectId = (int)(
    $values[$projectFkField]    ?? 
    $oldvalues[$projectFkField] ?? 0
);
if (!$projectId && $phaseId) {
    $sql = "SELECT ".$projectFkField." AS pid FROM phases WHERE ".$phasePkField."=".$phaseId;
    $log("lookup projectId: ".$sql);
    // Try via $conn first; fallback to DB::Query
    $rs = $conn ? $conn->query($sql) : DB::Query($sql);
    if ($rs) {
        $row = method_exists($rs,'fetchAssoc') ? $rs->fetchAssoc() : db_fetch_array($rs);
        if ($row && isset($row["pid"])) $projectId = (int)$row["pid"];
    }
}
$log("projectId=".$projectId);

// 3) if phase just completed, optionally close any open subphases in this phase
if ($autoCompleteSubs && $fieldChanged($completedDateField)) {
    $newCompleted = $values[$completedDateField] ?? null;
    $log("completed_date changed to ".(string)$newCompleted);
    if (!empty($newCompleted) && $phaseId > 0) {
        $escapedDate = $conn ? $conn->prepareString($newCompleted) : db_prepare_string($newCompleted);
        $sql = "
            UPDATE subphase_status st
            JOIN subphases s ON s.subphase_id = st.subphase_id
            SET
                st.complete_checked = 1,
                st.complete_date    = COALESCE(st.complete_date, ".$escapedDate.")
            WHERE s.phase_id = ".$phaseId."
              AND (st.complete_checked IS NULL OR st.complete_checked = 0)
        ";
        $log("auto-complete subphases SQL: ".$sql);
        DB::Exec($sql);
    }
}

// 4) choose a recalc function and call it (FORCE once for test)
$recalcFns = [
    'recalc_phase_due_dates',
    'recalc_project_schedule',
    'recalc_design_schedule'
];

$foundFn = null;
foreach ($recalcFns as $fn) {
    if (function_exists($fn)) { $foundFn = $fn; break; }
}
$log("recalc fn found: ".($foundFn ?: "NONE"));

if ($projectId > 0 && $foundFn) {
    // For test: force-call once so we can see if the function works at all
    $log("FORCE calling ".$foundFn."(".$projectId.")");
    $foundFn($projectId);
} else {
    $log("SKIP force-call: projectId=".$projectId." foundFn=".($foundFn?1:0));
}

// Also log whether the trigger logic *would* have called it normally
$log("shouldRecalc=".($shouldRecalc?1:0));

// DONE
return;

// Place event code here.
// Use "Add Action" button to add code snippets.

// If NOT an admin, hide full edit/add/delete, keep inline edit
if ( Security::isLoggedIn() && !Security::isAdmin() ) {
    $xt->assign("edit_link", false);        // hides full Edit icon
    $xt->assign("add_link", false);         // hides Add button (optional)
    $xt->assign("inlineadd_link", false);   // hides Inline Add (optional)
    $xt->assign("delete_link", false);      // hides Delete (optional)
    $xt->assign("inlineedit_link", true);   // keep inline edit available
}

// Place event code here.
// Use "Add Action" button to add code snippets.

(function(){
  function todayYMD(){
    var d=new Date(), y=d.getFullYear(), m=('0'+(d.getMonth()+1)).slice(-2), dd=('0'+d.getDate()).slice(-2);
    return y+'-'+m+'-'+dd; // DATE column format
  }

  // Get record id (recId) for the row that contains `el`
  function getRecId(el){
    try { if (window.Runner && Runner.getRecId){ var r=Runner.getRecId(el); if (r) return r; } } catch(e){}
    var $tr = $(el).closest("tr");
    return $tr.data("recordId") || $tr.attr("data-record-id") || (function(){
      var id = (el.id||""); var m=id.match(/_(\d+)$/); return m?m[1]:"";
    })();
  }

  // Set a date value using PHPR API if possible; otherwise force both hidden+visible inputs
  function setDate(field, recId, ymd){
    // 1) Preferred: control API (keeps widget + hidden in sync)
    try{
      if (window.pageObj && pageObj.getControl){
        var c = pageObj.getControl(field, recId);
        if (c && c.setValue){ c.setValue(ymd || ""); return true; }
      }
    }catch(e){}

    // 2) Fallback: set hidden post value + visible text input in that row/cell
    var $cell = $("tr[data-record-id='"+recId+"'] td[data-field='"+field+"']");
    if (!$cell.length) { // try without data-record-id
      $cell = $("#value_"+field+"_"+recId).closest("td[data-field='"+field+"']");
    }
    var $hid = $cell.find("input[type='hidden'][name='value_"+field+"_"+recId+"'], input[name='value_"+field+"_"+recId+"']");
    var $txt = $cell.find("input[type='text']");

    if ($hid.length) $hid.val(ymd || "");
    if ($txt.length) { $txt.val(ymd || "").trigger("change").trigger("blur"); }

    return ($hid.length || $txt.length) > 0;
  }

  // START checkbox handler
  $(document).on("change", "td[data-field='start_checked'] input[type=checkbox], input[id^='value_start_checked_']", function(){
    var recId = getRecId(this);
    if (this.checked) setDate("start_date", recId, todayYMD());
    else              setDate("start_date", recId, "");
  });

  // COMPLETED checkbox handler
  $(document).on("change", "td[data-field='completed_checked'] input[type=checkbox], input[id^='value_completed_checked_']", function(){
    var recId = getRecId(this);
    if (this.checked) setDate("completed_date", recId, todayYMD());
    else              setDate("completed_date", recId, "");
  });

  // Sync any already-open inline rows on load or after row (re)creation
  function initialSync(){
    $("td[data-field='start_checked'] input[type=checkbox], input[id^='value_start_checked_']").each(function(){
      var recId = getRecId(this);
      var $hid = $("input[name='value_start_date_"+recId+"']");
      if (this.checked && (!$hid.val())) setDate("start_date", recId, todayYMD());
    });
    $("td[data-field='completed_checked'] input[type=checkbox], input[id^='value_completed_checked_']").each(function(){
      var recId = getRecId(this);
      var $hid = $("input[name='value_completed_date_"+recId+"']");
      if (this.checked && (!$hid.val())) setDate("completed_date", recId, todayYMD());
    });
  }
  initialSync();
  $(document).on("click", ".rnr-inlinestart, .rnr-inlineedit, .rnr-inlineadd, .rnr-inlinecancel", function(){
    setTimeout(initialSync, 80);
  });
})();
