(function(){
  // Legend colors
  var C_OVERDUE  = '#ffcccc';
  var C_TODAY    = '#fff3cd';
  var C_UPCOMING = '#ffe8c6';

  function toInt(txt){
    var n = parseInt(String(txt||'').replace(/[^\-0-9]/g,''), 10);
    return isNaN(n) ? null : n;
  }

  function paint(){
    // Find any grid on the dashboard that has a days_until_due column
    jQuery("table.rnr-gridtable").each(function(){
      jQuery(this).find("tr").each(function(){
        var $tr   = jQuery(this);
        var $cell = $tr.find("td[data-field='days_until_due']");
        if (!$cell.length) return;                 // not our grid/row

        var n = toInt($cell.text());
        if (n === null){ $tr.css('background-color',''); return; }

        if (n < 0)       $tr.css('background-color', C_OVERDUE);   // Overdue
        else if (n === 0)$tr.css('background-color', C_TODAY);     // Today
        else             $tr.css('background-color', C_UPCOMING);  // Next N days
      });
    });
  }

  // Initial paint (and a tiny delayed pass in case the grid finishes rendering after OnLoad)
  paint();
  setTimeout(paint, 120);

  // Repaint after common dashboard interactions / ajax loads
  jQuery(document).on('click', ".rnr-button, .pagination a, .rnr-orderlink, .rnr-expand, .rnr-collapse", function(){
    setTimeout(paint, 120);
  });

  // Repaint when blocks/rows get inserted dynamically
  try{
    var obs = new MutationObserver(function(){ setTimeout(paint, 60); });
    obs.observe(document.body, {childList:true, subtree:true});
  }catch(e){}
})();

